// reducerは、定義したアクションをDispatchするためのもの

const initialState = {
    todoList: []
}

export const todoReducer = (state = {}) => {
    switch (action.type) {

        // action.type別で処理を分岐（ADD_TODOの場合）
        case 'ADD_TODO':
            // 新しく追加するTODO
            const todo = action.payload.todo;
            //　現在、Listにある（state）TODOを複製
            const newState = Object.assign({}, state);
            // 複製したものに、新しいTodoを追加する。
            newState.todoList.push(todo);

        // action.type別で処理を分岐（変化なしの場合）
        default:
            return state;
    }
};
